mapper.insert:
insert into tb_article_comment_like(user_id, comment_id, create_time) select temp2.fromId,temp2.toId, temp2.create_time from (select temp.fromId,temp.toId,temp.create_time from(
select #{item.fromId} as fromId,#{item.toId} as toId,#{item.createTime} as create_time
)as temp left join tb_user as tu on tu.id=temp.fromId left join tb_article_comment as tac on tac.article_comment_id=temp.toId where tu.id is not null and tac.article_comment_id is not null)as temp2

mapper.update:
update tb_article_comment
set like_num=like_num + #{num}
where article_comment_id = #{id}

mapper.delete:
start transaction;
delete
from tb_article_comment
where article_comment_id = #{id};
delete
from tb_reply_comment
where comment_id = #{id};
commit;

    start transaction ;
    delete from tb_article_comment where article_comment_id in

        #{id}

    delete from tb_reply_comment where comment_id in 
    #{id}
;

delete from tb_article_comment_like where comment_id in (
select temp.comment_id from(

    select comment_id from tb_article_comment_like where comment_id=#{item.toId} and user_id=#{item.fromId}
)temp
)

mapper.select:
select count(*)
from tb_article_comment_like
where comment_id = #{commentId}
  and user_id = #{userId}

select count(*)
from tb_article_comment_like
where user_id = #{userId}
  and comment_id = #{commentId}
